import { useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faRotate,
  faCoins,
} from "@fortawesome/free-solid-svg-icons";
import "./average.css";

const Average = () => {
  const [inputList, setInputList] = useState([{ rate: "", amount: "" }]);

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...inputList];
    list[index][name] = value;
    setInputList(list);
  };

  const handleAddClick = () => {
    setInputList([...inputList, { rate: "", amount: "" }]);
  };

  const handleRemoveClick = (index) => {
    const list = [...inputList];
    list.splice(index, 1);
    setInputList(list);
  };

  const calculateAverageRate = () => {
    let totalRate = 0;
    let totalAmount = 0;

    inputList.forEach((input) => {
      if (input.rate && input.amount) {
        totalRate += parseFloat(input.rate) * parseFloat(input.amount);
        totalAmount += parseFloat(input.amount);
      }
    });

    return totalAmount === 0 ? 0 : totalRate / totalAmount;
  };

  return (
    <div className="AverageBox">
      <div className="titleBox">
        <h2>
          <Link to="/">
            <FontAwesomeIcon icon={faRotate} /> Converter
          </Link>
        </h2>
        <h2>
          <FontAwesomeIcon icon={faCoins} /> Average
        </h2>
      </div>
      <div className="changeBox">
        <div className="costTitle">
          <h3>Rate</h3>
          <h3>Amount</h3>
        </div>

        {inputList.map((x, i) => {
          return (
            <div className="costInput" key={i}>
              <input
                name="rate"
                placeholder="Rate"
                value={x.rate}
                onChange={(e) => handleInputChange(e, i)}
              />
              <input
                name="amount"
                placeholder="Amount"
                value={x.amount}
                onChange={(e) => handleInputChange(e, i)}
              />
              {inputList.length > 1 && (
                <button onClick={() => handleRemoveClick(i)}>Remove</button>
              )}
            </div>
          );
        })}

        <hr></hr>
        <button onClick={handleAddClick}>+ More</button>

        <h3>Average purchase rate</h3>
        <div>{calculateAverageRate().toFixed(2)}</div>
      </div>
    </div>
  );
};

export default Average;
