import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRotate, faCoins } from "@fortawesome/free-solid-svg-icons";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [selectedCurrencies, setSelectedCurrencies] = useState([]);
    const [amounts, setAmounts] = useState(Array(5).fill(""));
    const [rates, setRates] = useState({});

    useEffect(() => {
        const fetchCurrencies = async () => {
            const response = await fetch("https://zoelindev.com/Real-time-Currency/api/currency_code.php");
            const data = await response.json();
            setCurrencies(data.currency_codes);
            setSelectedCurrencies(Array(5).fill(data.currency_codes[0]));
            setRates(data.rate);
        };

        fetchCurrencies();
    }, []);

    const handleCurrencyChange = (index, newCurrency) => {
        setSelectedCurrencies((prevCurrencies) => {
            const updatedCurrencies = [...prevCurrencies];
            updatedCurrencies[index] = newCurrency;
            return updatedCurrencies;
        });
        updateConvertedAmounts(index);
    };

    const handleAmountChange = (index, newAmount) => {
        setAmounts((prevAmounts) => {
            const updatedAmounts = [...prevAmounts];
            updatedAmounts[index] = newAmount;
            return updatedAmounts;
        });
    };

    useEffect(() => {
        updateConvertedAmounts(0);
    }, [amounts[0], selectedCurrencies]);




    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            {amounts.map((amount, index) => (
                <div className="group" key={index}>
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => {
                            handleAmountChange(index, e.target.value);
                            if (index === 0) updateConvertedAmounts(index);
                        }}
                    />
                    <CustomSelect
                        options={currencies}
                        value={selectedCurrencies[index]}
                        onChange={(newCurrency) => {
                            handleCurrencyChange(index, newCurrency);
                            updateConvertedAmounts(index);
                        }}
                    />
                </div>
            ))}
        </div>
    )
};

export default Converter;
