import { useState } from "react";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [rates, setRates] = useState([]);
    const [currency1, setCurrency1] = useState('USD');
    const [currency2, setCurrency2] = useState('EUR');

    useEffect(() => {
        const fetchCurrencies = async () => {
            const response = await fetch("https://zoelindev.com/Real-time-Currency/api/currency_code.php");
            const data = await response.json();
            setCurrencies(data.currency_codes);
            setRates(data.rate);
        };

        fetchCurrencies();
    }, []);

    const handleCurrency1Change = (currency) => {
        setCurrency1(currency);
    };

    const handleCurrency2Change = (currency) => {
        setCurrency2(currency);
    };

    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>
            <CustomSelect options={currencies} onOptionClick={handleCurrency1Change} />
            <CustomSelect options={currencies} onOptionClick={handleCurrency2Change} />
            <input type="number" />
        </div>
    )
};

export default Converter;
