import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
//import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faRotate,
    faCoins
} from "@fortawesome/free-solid-svg-icons";


const Converter = () => {

    const [currencies, setCurrencies] = useState([]);

    useEffect(() => {
        const apiKey = "6ouIhq8dNQqxffVKZX4k4NgVgF4anHWm";
        const to = 'EUR';  // 你要轉換的貨幣類型
        const from = 'USD';  // 你要轉換的貨幣類型
        const amount = 10;  // 你要轉換的貨幣金額
        const url = `https://api.apilayer.com/fixer/convert?to=${to}&from=${from}&amount=${amount}`;
        const myHeaders = new Headers();
        myHeaders.append("apikey", apiKey);

        const requestOptions = {
          method: 'GET',
          headers: myHeaders,
          redirect: 'follow'
        };

        fetch(url, requestOptions)
          .then(response => response.text())
          .then(result => console.log(result))
          .catch(error => console.log('error', error));

         // Fetch all currency symbols
        fetch("https://api.apilayer.com/fixer/symbols", requestOptions)
             .then(response => response.json())
             .then(result => {
                 const currencyArray = Object.keys(result.symbols);
                 setCurrencies(currencyArray);
             })
             .catch(error => console.log('error', error));
      }, []);

      const CustomSelect = ({ options }) => {
        const [isOpen, setIsOpen] = useState(false);
        const [selectedOption, setSelectedOption] = useState(null);

        const handleOptionClick = (option) => {
            setSelectedOption(option);
            setIsOpen(false);
        };
        return (
            <div className="custom-select" onClick={() => setIsOpen(!isOpen)}>
                {selectedOption ? (
                    <div className="selected-option">
                        <img src={`/flags/${selectedOption}.png`} alt={selectedOption} />
                        {selectedOption}
                    </div>
                ) : (
                    <div className="selected-option">Select a currency</div>
                )}
                {isOpen && (
                    <ul className="options-list">
                        {options.map((option, index) => (
                            <li
                                key={index}
                                onClick={() => handleOptionClick(option)}
                                className="option"
                            >
                                <img src={`/flags/${option}.png`} alt={option} />
                                {option}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        );
    };


    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            {[...Array(5)].map((_, index) => (
                <div className="group" key={index}>
                    <input type="text" />
                    <CustomSelect options={currencies} />
                </div>
            ))}
        </div>
    );
}

export default Converter;
