import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRotate, faCoins } from "@fortawesome/free-solid-svg-icons";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [rates, setRates] = useState([]);
    const [fromCurrency, setFromCurrency] = useState("");
    const [toCurrency, setToCurrency] = useState("");

    useEffect(() => {
        const fetchCurrencies = async () => {
            const response = await fetch("https://zoelindev.com/Real-time-Currency/api/currency_code.php");
            const data = await response.json();
            setCurrencies(data.currency_codes);
            setRates(data.rate);
            console.log(rates);
        };

        fetchCurrencies();
    }, []);

    const handleFromCurrencyChange = (currency) => {
        setFromCurrency(currency);
    };

    const handleToCurrencyChange = (currency) => {
        setToCurrency(currency);
    };

    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            <div className="currency-selectors">
                <CustomSelect options={currencies} onSelect={handleFromCurrencyChange} />
                <CustomSelect options={currencies} onSelect={handleToCurrencyChange} />
            </div>

            <input type="number" />

        </div>
    );
};

export default Converter;
