import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
//import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faRotate,
    faCoins
} from "@fortawesome/free-solid-svg-icons";


const Converter = () => {

    const [currencies, setCurrencies] = useState([]);

    useEffect(() => {
        const apiKey = "6ouIhq8dNQqxffVKZX4k4NgVgF4anHWm";
        const to = 'EUR';  // 你要轉換的貨幣類型
        const from = 'USD';  // 你要轉換的貨幣類型
        const amount = 10;  // 你要轉換的貨幣金額
        const url = `https://api.apilayer.com/fixer/convert?to=${to}&from=${from}&amount=${amount}`;
        const myHeaders = new Headers();
        myHeaders.append("apikey", apiKey);

        const requestOptions = {
          method: 'GET',
          headers: myHeaders,
          redirect: 'follow'
        };

        fetch(url, requestOptions)
          .then(response => response.text())
          .then(result => console.log(result))
          .catch(error => console.log('error', error));

         // Fetch all currency symbols
        fetch("https://api.apilayer.com/fixer/symbols", requestOptions)
             .then(response => response.json())
             .then(result => {
                 const currencyArray = Object.keys(result.symbols);
                 setCurrencies(currencyArray);
             })
             .catch(error => console.log('error', error));
      }, []);

    return(
	<div className="ConverterBox">
        <h2>
            <FontAwesomeIcon icon={faRotate} /> Converter
        </h2>
        <h2>
            <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
        </h2>

        {[...Array(5)].map((_, index) => (
            <div className="group" key={index}>
                <input type="text" />
                <select>
                    {currencies.map((currency, i) => (
                        <option value={currency} key={i}>
                            {currency}
                        </option>
                    ))}
                </select>
            </div>
        ))}

	</div>
    );
}

export default Converter;
