import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faRotate,
    faCoins
} from "@fortawesome/free-solid-svg-icons";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [selectedCurrencies, setSelectedCurrencies] = useState([]);
    const [amounts, setAmounts] = useState(Array(5).fill(''));
    const [rates, setRates] = useState({});

    useEffect(() => {
        const fetchCurrencies = async () => {
            const response = await fetch('https://zoelindev.com/Real-time-Currency/api/currency_code.php');
            const data = await response.json();
            setCurrencies(data.currency_codes);
            setSelectedCurrencies(Array(5).fill(data.currency_codes[0]));
            setRates(data.rate);
        };

        fetchCurrencies();
    }, []);

    const handleCurrencyChange = (index, newCurrency) => {
        setSelectedCurrencies(prevCurrencies => {
            const updatedCurrencies = [...prevCurrencies];
            updatedCurrencies[index] = newCurrency;
            return updatedCurrencies;
        });
    };

    const handleAmountChange = (index, newAmount) => {
        setAmounts(prevAmounts => {
            const updatedAmounts = [...prevAmounts];
            updatedAmounts[index] = isNaN(parseFloat(newAmount)) ? "" : parseFloat(newAmount);
            return updatedAmounts;
        });
    };

    const convertAmount = (sourceIndex, targetIndex) => {
        const sourceCurrency = selectedCurrencies[sourceIndex];
        const targetCurrency = selectedCurrencies[targetIndex];
        const amount = amounts[sourceIndex];

        if (!rates || !rates[`USD${sourceCurrency}`] || !rates[`USD${targetCurrency}`]) {
            return "";
        }

        const sourceToUSD = rates[`USD${sourceCurrency}`].Exrate;
        const usdToTarget = rates[`USD${targetCurrency}`].Exrate;

        const convertedAmount = (amount / sourceToUSD) * usdToTarget;
        return isNaN(convertedAmount) ? "" : convertedAmount.toFixed(2);
    };

    const updateConvertedAmounts = (activeIndex) => {
        setAmounts((prevAmounts) =>
            prevAmounts.map((_, index) =>
                index === activeIndex
                    ? prevAmounts[activeIndex]
                    : convertAmount(activeIndex, index)
            )
        );
    };

    useEffect(() => {
        updateConvertedAmounts(amounts.findIndex((amount) => amount !== ""));
    }, [selectedCurrencies, rates]);

    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            {amounts.map((amount, index) => (
                <div className="group" key={index}>
                    <input
                        type="text"
                        value={amount}
                        onChange={(e) => {
                            handleAmountChange(index, e.target.value);
                            updateConvertedAmounts(index);
                        }}
                    />
                    <CustomSelect
                        options={currencies}
                        value={selectedCurrencies[index]}
                        onChange={(newCurrency) => {
                            handleCurrencyChange(index, newCurrency);
                            updateConvertedAmounts(index);
                        }}
                    />
                </div>
            ))}
        </div>
    );
};

export default Converter;
