import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRotate, faCoins } from "@fortawesome/free-solid-svg-icons";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [rates, setRates] = useState([]);
    const [conversionList, setConversionList] = useState([
        { id: 1, value: "", selectedCurrency: null },
        { id: 2, value: "", selectedCurrency: null },
        { id: 3, value: "", selectedCurrency: null },
        { id: 4, value: "", selectedCurrency: null },
        { id: 5, value: "", selectedCurrency: null },
    ]);

    useEffect(() => {
        const fetchCurrencies = async () => {
            const response = await fetch("https://zoelindev.com/Real-time-Currency/api/currency_code.php");
            const data = await response.json();
            setCurrencies(data.currency_codes);
            setRates(data.rate);
            console.log(rates);
        };

        fetchCurrencies();
    }, []);

    const handleCurrencyChange = (currency, id) => {
        const updatedConversionList = [...conversionList];
        const conversion = updatedConversionList.find((item) => item.id === id);
        conversion.selectedCurrency = currency;
        setConversionList(updatedConversionList);
    };

    const handleInputChange = (value, id) => {
        const updatedConversionList = [...conversionList];
        const conversion = updatedConversionList.find((item) => item.id === id);
        conversion.value = value;
        setConversionList(updatedConversionList);
    };

    const handleSwap = (id) => {
        const updatedConversionList = [...conversionList];
        const conversion = updatedConversionList.find((item) => item.id === id);
        const temp = conversion.fromCurrency;
        conversion.fromCurrency = conversion.toCurrency;
        conversion.toCurrency = temp;
        conversion.value = "";
        setConversionList(updatedConversionList);
    };

    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            {conversionList.map((conversion) => (
                <div key={conversion.id} className="conversion">
                    <input
                        type="number"
                        value={conversion.value}
                        onChange={(event) =>
                            handleInputChange(event.target.value, conversion.id)
                        }
                    />

                    <CustomSelect
                        options={currencies}
                        onSelect={(currency) => handleCurrencyChange(currency, conversion.id)}
                        selectedOption={conversion.selectedCurrency}
                    />

                    <button onClick={() => handleSwap(conversion.id)}>Swap</button>
                </div>
            ))}
        </div>
    );
};

export default Converter;
