import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faRotate,
    faCoins
} from "@fortawesome/free-solid-svg-icons";
import CustomSelect from './CustomSelect';

const Converter = () => {
    const [currencies, setCurrencies] = useState([]);
    const [amounts, setAmounts] = useState([1, 0, 0, 0, 0]);
    const [selectedCurrencies, setSelectedCurrencies] = useState(Array(5).fill('USD'));

    useEffect(() => {
        // Fetch all currency symbols
        const myHeaders = new Headers();
        myHeaders.append("apikey", "6ouIhq8dNQqxffVKZX4k4NgVgF4anHWm");
        const requestOptions = {
            method: 'GET',
            headers: myHeaders,
            redirect: 'follow'
        };
        fetch("https://api.apilayer.com/fixer/symbols", requestOptions)
            .then(response => response.json())
            .then(result => {
                const currencyArray = Object.keys(result.symbols);
                setCurrencies(currencyArray);
            })
            .catch(error => console.log('error', error));
    }, []);

    useEffect(() => {
        updateAllAmounts(0);
    }, [selectedCurrencies]);

    const updateAllAmounts = (sourceIndex) => {
        const sourceAmount = amounts[sourceIndex];
        const sourceCurrency = selectedCurrencies[sourceIndex];
        const promises = selectedCurrencies.map((targetCurrency, index) => {
            if (index === sourceIndex) return Promise.resolve(sourceAmount);
            return fetchConversionRate(sourceCurrency, targetCurrency)
                .then(rate => sourceAmount * rate);
        });

        Promise.all(promises).then(newAmounts => {
            setAmounts(newAmounts);
        });
    };

    const fetchConversionRate = async (from, to) => {
        const apiKey = "6ouIhq8dNQqxffVKZX4k4NgVgF4anHWm";
        const url = `https://api.apilayer.com/fixer/convert?to=${to}&from=${from}&amount=1`;
        const myHeaders = new Headers();
        myHeaders.append("apikey", apiKey);

        const requestOptions = {
            method: 'GET',
            headers: myHeaders,
            redirect: 'follow'
        };

        const response = await fetch(url, requestOptions);
        const result = await response.json();
        return result.result;
    };

    const handleAmountChange = (index, newAmount) => {
        setAmounts(prevAmounts => {
            const newAmounts = [...prevAmounts];
            newAmounts[index] = parseFloat(newAmount) || 0;
            return newAmounts;
        });
        updateAllAmounts(index);
    };

    const handleCurrencyChange = (index, newCurrency) => {
        setSelectedCurrencies(prevCurrencies => {
            const newSelectedCurrencies = [...prevCurrencies];
            newSelectedCurrencies[index] = newCurrency;
            return newSelectedCurrencies;
        });
    };

    return (
        <div className="ConverterBox">
            <h2>
                <FontAwesomeIcon icon={faRotate} /> Converter
            </h2>
            <h2>
                <Link to="/average"><FontAwesomeIcon icon={faCoins} /> Average</Link>
            </h2>

            {amounts.map((amount, index) => (
                <div className="group" key={index}>
                    <input
                        type="text"
                        value={amount}
                        onChange={e => handleAmountChange(index, e.target.value)}
                    />
                    <CustomSelect
                        options={currencies}
                        value={selectedCurrencies[index]}
                        onChange={newCurrency => handleCurrencyChange(index, newCurrency)}
                    />
                </div>
            ))}
        </div>
    );
}

export default Converter;
